答案：
```ts
type LetterCounter = {
  // A-Z
  [i: string]: number
}

function letterAddCount(target: LetterCounter, source: LetterCounter) {
  for (let k in source) {
    target[k] ??= 0
    target[k] += source[k]
  }
  return target
}
function letterMultipleCount(target: LetterCounter, multiples: number) {
  for (let i in target) {
    target[i] *= multiples
  }
  return target
}
function countOfLetters(str: string) {
  const regex = /[1-9]/
  const stack: LetterCounter[] = [{}]
  for (let i = 0; i < str.length; i++) {
    const ch = str[i]
    let count = 1
    if (regex.test(str[i + 1])) count = +str[++i]
    // case ( | )
    switch (ch) {
      case '(':
        stack.push({})
        continue
      case ')':
        const pop = stack.pop()!
        const last = stack[stack.length - 1]
        letterAddCount(last, letterMultipleCount(pop, count))
        continue
    }
    // case A-Z
    const last = stack[stack.length - 1]
    last[ch] ??= 0
    last[ch] += count
  }
  return stack.pop()
}
```

