function decimalToBinary (num) {
    let negative = false
    if (num < 0) {
        num = -num
        negative = true
    }
    num += ''
    let point = num.indexOf('.')
    let int
    let intBinary = ''
    let float
    let floatBinary = ''

    if (point > -1) {
        int = num.slice(0, point)
        float = Number(`0${num.slice(point)}`)
    } else {
        int = num
    }
    if(int) {
        int = +int
        while (int >= 1) {
            intBinary = int % 2 + intBinary
            int = int / 2 | 0
        }
    }

    if (float) {
        while(float) {
            float *= 2
            if (float >= 1) {
                floatBinary += 1
            } else {
                floatBinary += 0
            }
            float = float % 1
        }
    }
    let result = ''
    if (negative) {
        result += '-'
    }
    result += (`${intBinary || 0}.${floatBinary}`)
    return result
}

