function solution(n) {
    const dp = Array.from({ length: 2 }, () => new Array(n + 1).fill(0));
    for (let i = 1; i <= n; i++) {
        dp[0][i] = i;
        dp[1][i] = n;
        for (let j = 1; j <= i; j++) {
            dp[1][i] = Math.min(dp[1][i], Math.max(dp[0][j - 1], dp[1][i - j]) + 1);
        }
    }
    return dp[1][n - 1];
}
